#include <stdio.h>
#include "../get_next_line.h"


void gnl(int fd, char * str)
{
	char * line;


	line = get_next_line(fd);
//	printf("%s", line);
	if (!line && !str)
		printf("1 & NULLIK\n");
	else
		printf("%d\n", !strcmp(line, str));
	free(line);
}

int main()
{
	//int fd = open("abc.txt", O_RDONLY);
	int fd = open("/home/kali/Desktop/get_next_line/trip/files/big_line_no_nl", O_RDONLY);
	gnl(fd, "");

	close(fd);
	//system("leaks a.out");
}
